    }

    const addEmoji = () => {
        let copy = [...grille]
        for (let i=0; i < copy[i].lenght; i++) {
            for (let j=0; j < copy[i].length; j++)
            if (copy[j][i] === ""){
                copy[i][j] = "X";
                break
            }
        }
    }

    return (
        <div className="d-flex flex-column align-items-center">
            <table className="garden mt-5">
                <tbody>
                    {grille.map((row, i) => {
                        return (
                            <tr key={i}>
                                {row.map((cell, j) => {
                                    return <td key={j}>{cell}</td>;
                                })}
                            </tr>)
                    })}
                </tbody>
            </table>
            <button className="btn btn-primary mt-5">Plant an emoji</button>
        </div>
    )
}
