import React, { useState } from 'react';
import './App.css';
import GenerateGarden from "./component/GenerateGarden";
import ThemeContext from "./ThemeContext"


function ThemeChoice(props) {
  const handleChange = event => {
    const value = event.currentTarget.value;
    props.updateTheme(value);
  }
  return (
    <select name="theme" defaultValue={props.theme} onChange={handleChange}>
      <option value="Plants">Plants(défault)</option>
      <option value="Emoticones">Emoticones</option>
      <option value="Weather">Weather</option>
    </select>

  )
}

function SpeedChoice(props) {
  const handleChange = event => {
    const value = event.currentTarget.value;
    props.updateSpeed(value);
  }
  return (
    <select name="speed" defaultValue={props.speed} onChange={handleChange}>
      <option value="twosec">2 seconds</option>
      <option value="fivesec">5 seconds</option>
      <option value="sevensec">7 seconds</option>
      <option value="tensec">10 seconds</option>
    </select>
  )
}

function Toolbar(props) {
  return (
    <div>
      <ThemeChoice theme={props.theme} updatetheme={props.updateTheme} />
      <SpeedChoice speed={props.speed} updatespeed={props.updateSpeed} />
    </div>
  )
}

