import React, { useState, useContext } from 'react';
import ThemeContext from "../ThemeContext"

export default function SpeedChoice(props) {

  const { speed, updateSpeed } = useContext(ThemeContext);

  const handleChange = event => {
    const value = event.currentTarget.value;
    updateSpeed(value);
  }
  return (
    <select name="speed" defaultValue={speed} onChange={handleChange}>
      <option value="twosec">2 seconds</option>
      <option value="fivesec">5 seconds</option>
      <option value="sevensec">7 seconds</option>
      <option value="tensec">10 seconds</option>
    </select>
  )
}
