import React, { useState, useContext } from 'react';
import './App.css';
import GenerateGarden_plants from "./component/GenerateGarden_plants";
import GenerateGarden_emoticones from "./component/GenerateGarden_emoticones";
import GenerateGarden_wtf from "./component/GenerateGarden_wtf";
import ThemeContext from "./ThemeContext"


function ThemeChoice(props) {

  const { theme, updateTheme } = useContext(ThemeContext);

  const handleChange = event => {
    const value = event.currentTarget.value;
    updateTheme(value);
  }
  return (
    <select name="theme" defaultValue={theme} onChange={handleChange}>
      <option value="Plants">Plants(d√©fault)</option>
      <option value="Emoticones">Emoticones</option>
      <option value="Weather">Weather</option>
    </select>

  )
}

function SpeedChoice(props) {

  const { speed, updateSpeed } = useContext(ThemeContext);

  const handleChange = event => {
    const value = event.currentTarget.value;
    updateSpeed(value);
  }
  return (
    <select name="speed" defaultValue={speed} onChange={handleChange}>
      <option value="twosec">2 seconds</option>
      <option value="fivesec">5 seconds</option>
      <option value="sevensec">7 seconds</option>
      <option value="tensec">10 seconds</option>
    </select>
  )
}

function Toolbar(props) {
  return (
    <div>
      <ThemeChoice />
      <SpeedChoice />
    </div>
  )
}

function App() {

  const [theme, setTheme] = useState(<GenerateGarden_plants/>)

  const contextValue = {
    theme,
    updateTheme: setTheme
  }

  return (
    <ThemeContext.Provider value={contextValue}>
      <div className="App">
        <h1>My Emoji Garden</h1>
        <div className={theme}>
          <Toolbar />
          <p>{<GenerateGarden_plants/>}</p>
        </div>
      </div>
    </ThemeContext.Provider>
  );
}

export default App;
