import React, { useState, useContext } from 'react';
import './App.css';
import GenerateGarden from "./component/GenerateGarden";
import ThemeContext from "./ThemeContext"


function ThemeChoice(props) {

  const contextValue = useContext(ThemeContext)

  const handleChange = event => {
    const value = event.currentTarget.value;
    contextValue.updateTheme(value);
  }
  return (
    <select name="theme" defaultValue={contextValue.theme} onChange={handleChange}>
      <option value="Plants">Plants(d√©fault)</option>
      <option value="Emoticones">Emoticones</option>
      <option value="Weather">Weather</option>
    </select>

  )
}

function SpeedChoice(props) {

  const contextValue = useContext(ThemeContext)

  const handleChange = event => {
    const value = event.currentTarget.value;
    contextValue.updateSpeed(value);
  }
  return (
    <select name="speed" defaultValue={contextValue.speed} onChange={handleChange}>
      <option value="twosec">2 seconds</option>
      <option value="fivesec">5 seconds</option>
      <option value="sevensec">7 seconds</option>
      <option value="tensec">10 seconds</option>
    </select>
  )
}

function Toolbar(props) {
  return (
    <div>
      <ThemeChoice />
      <SpeedChoice  />
    </div>
  )
}

function App() {

  const [theme, setTheme] = useState("Plants")

  const contextValue = {
    theme,
    updateTheme: setTheme
  }

  return (
    <ThemeContext.Provider value={contextValue}>
      <div className="App">
        <h1>My Emoji Garden</h1>
        <div className={theme}>
          <Toolbar theme={theme} updateTheme={setTheme} />
          <p>Theme utilis√© : {theme}</p>
        </div>
        <div className="Score">
          <option value="icon1">icon1</option>
          <option value="icon2">icon2</option>
          <option value="icon3">icon3</option>
        </div>
        <GenerateGarden />
      </div>
    </ThemeContext.Provider>
  );
}

export default App;
