import React, { useState } from 'react';
import './App.css';
import ReactDOM from ''
import GenerateGarden from "./component/GenerateGarden";


// const contextmarks = React.createContext(null);
// Contexte défini avec les propriétés de l’interface MarksContext initialisées avec null

// const MarksContextProvider = contextmarks.Provider;
// C’est le magasin dans lequel les états seront conservés et passés en tant que props.

// const MarksContextConsumer = contextmarks.Consumer;
// C’est le magasin à partir duquel les états stockés dans le fournisseur
// Les contextes seront extraits par le composant enfant et utilisés en fonction de l’utilisateur.

function App() {
  return (
    <div className="App">
      <h1>My Emoji Garden</h1>
      <div className="Parametres">
        <div className="Theme">
          Theme:
          <select id="dropdown">
            <option value="Plants">Plants(défault)</option>
            <option value="Emoticones">Emoticones</option>
            <option value="Weather">Weather</option>
          </select>
        </div>
        <div className="Speed">
          Speed:
          <select id="dropdown">
            <option value="twosec">2 seconds</option>
            <option value="fivesec">5 seconds</option>
            <option value="sevensec">7 seconds</option>
            <option value="tensec">10 seconds</option>
          </select>
        </div>
      </div>
      <div className="Score">
        <option value="icon1">icon1</option>
        <option value="icon2">icon2</option>
        <option value="icon3">icon3</option>
      </div>
      <GenerateGarden />
    </div>
  );
}

export default App;
